pragma solidity 0.8.25;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";

interface IRouter {
	function getBpsFee() external view returns (uint256);
}

contract Agent {
	using SafeERC20 for IERC20Metadata;
	using Address for address payable;

	address public owner;
	address private router;

	IERC20Metadata internal constant USDC =
		IERC20Metadata(0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174);
	IERC20Metadata internal constant WNATIVE =
		IERC20Metadata(0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270);

	ISwapRouter public immutable uniswapRouter =
		ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);

	uint256 internal constant _DUST = 10;
	uint256 internal constant _BPS_BASE = 10000;

	struct Call {
		address to;
		uint256 value;
		bytes data;
	}

	constructor(address _owner, address _router) {
		require(msg.sender == _router, "Only Router can instantiate");
		owner = _owner;
		router = _router;
	}

	modifier onlyRouter() {
		require(msg.sender == router, "Callable only by the router");
		_;
	}

	function execute(
		Call[] calldata calls,
		address[] calldata tokensReturn
	) external onlyRouter {
		for (uint i = 0; i < calls.length; i++) {
			(bool success, ) = calls[i].to.call{ value: calls[i].value }(
				calls[i].data
			);
			require(success, "Batch call failed");
		}

		_swapTokensForUSDC(tokensReturn);
		_returnTokens(tokensReturn);
	}

	function _swapTokensForUSDC(address[] calldata tokens) internal {
		uint256 routerBpsFee = IRouter(router).getBpsFee();

		for (uint i = 0; i < tokens.length; i++) {
			IERC20Metadata token = IERC20Metadata(tokens[i]);
			uint tokenBalance = token.balanceOf(address(this));

			if (tokenBalance == 0) continue;

			uint amountAfterFee = (tokenBalance * (_BPS_BASE - routerBpsFee)) /
				_BPS_BASE;
			if (amountAfterFee == 0 || address(token) == address(USDC)) {
				if (amountAfterFee != 0) {
					token.transfer(router, amountAfterFee);
				}
				continue;
			}

			token.approve(address(uniswapRouter), amountAfterFee);

			// Attempt direct swap, if it fails, then try multi-hop swap
			if (!trySwapDirect(token, amountAfterFee)) {
				if (!tryMultiHopSwap(token, amountAfterFee)) {
					token.transfer(owner, amountAfterFee); // If both swaps fail, return tokens to owner
				}
			}

			token.approve(address(uniswapRouter), 0); // Always revoke approval to minimize risk
		}
	}

	function trySwapDirect(
		IERC20Metadata token,
		uint amount
	) private returns (bool) {
		try
			uniswapRouter.exactInputSingle(
				ISwapRouter.ExactInputSingleParams({
					tokenIn: address(token),
					tokenOut: address(USDC),
					fee: 3000,
					recipient: address(this),
					deadline: block.timestamp,
					amountIn: amount,
					amountOutMinimum: 0,
					sqrtPriceLimitX96: 0
				})
			)
		returns (uint) {
			return true;
		} catch {
			return false;
		}
	}

	function tryMultiHopSwap(
		IERC20Metadata token,
		uint amount
	) private returns (bool) {
		bytes memory path = abi.encodePacked(
			address(token),
			uint24(3000),
			address(WNATIVE),
			uint24(3000),
			address(USDC)
		);
		try
			uniswapRouter.exactInput(
				ISwapRouter.ExactInputParams({
					path: path,
					recipient: address(this),
					deadline: block.timestamp,
					amountIn: amount,
					amountOutMinimum: 0
				})
			)
		returns (uint) {
			return true;
		} catch {
			return false;
		}
	}

	function _returnTokens(address[] calldata tokensReturn) internal {
		for (uint256 i = 0; i < tokensReturn.length; i++) {
			address token = tokensReturn[i];
			uint256 balance = IERC20Metadata(token).balanceOf(address(this));
			if (balance > _DUST) {
				IERC20Metadata(token).safeTransfer(owner, balance);
			}
			if (address(this).balance > 0) {
				payable(owner).sendValue(address(this).balance);
			}
		}
	}
}

